class Multilingual{constructor(t,s){this.lang=t||navigator.language||"en",this.translations={},this.loadTranslations(t,s).then((()=>this.createGetters()))}async loadTranslations(t,s){try{const a=`${s}/${t}.json`,n=await fetch(a);this.translations=await n.json()}catch(t){console.error("Failed to load translations",t)}}setLocale(t){this.lang=t}createGetters(){const t=Object.keys(this.translations);for(const s of t)Object.defineProperty(this,s,{get:function(){return this.translations[s]}})}}